implementation.constructor:
if (window.tabsOnBottom) {
    return;
}

window.tabsOnBottom = true;

(function(document) {
    let tabsToolbar = document.getElementById("TabsToolbar");
    let navigatorToolbox = document.getElementById("navigator-toolbox");
    let fragment = document.createDocumentFragment();

    tabsToolbar.classList.remove("titlebar-color");
    fragment.appendChild(tabsToolbar);
    navigatorToolbox.appendChild(fragment);
})(window.document);
